import React from "react";
import ConditionForm from "./formElements/ConditionForm";

class App extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            ruleName: "",
            ruleConditions: [
                {
                    conditionType: "",
                    conditionCount: "1",
                    conditionObject: "-- select something --"
                }
            ]
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeInForm = this.handleChangeInForm.bind(this);
    }

    handleChange(event)
    {
        const {name, value} = event.target;
        this.setState(() => { return( {[name]: value} ); });
    }
    handleChangeInForm(event)
    {
        const {name, value, formindex} = event.target;
        this.setState((prevState) => {
            let newRuleConditions = prevState.ruleConditions.slice();
            console.log(name);
            let condition = {
                ...newRuleConditions[formindex],
                [name]: value
            }
            newRuleConditions[formindex] = condition;

            console.log(prevState);
            console.log({ruleConditions: newRuleConditions});

            return ({ruleConditions: newRuleConditions});
        });
        console.log("hit end");
    }

    addCondition()
    {

    }
    removeCondition(id)
    {

    }

    render()
    {
        let preparedConditions = []
        for (let i = 0; i < this.state.ruleConditions.length; i++)
        {
            preparedConditions.push(
                <ConditionForm 
                    key={i} 
                    formindex={i}
                    conditionType={this.state.ruleConditions[i].conditionType}
                    conditionCount={this.state.ruleConditions[i].conditionCount}
                    conditionObject={this.state.ruleConditions[i].conditionObject}
                    onRemove={this.state.removeCondition}
                    onChange={this.handleChangeInForm}
                />
            );
        }


        return(
            <div>
                <h1>Ivolunteer rule editor prototype</h1>
                <hr />
                <h2><span style={{color: "#ff0000"}}>rule name: </span></h2>
                <h2>
                    <input 
                        name="ruleName"
                        id="ruleNameTxt" 
                        placeholder="enter the name of your rule" 
                        type="text" 
                        value={this.state.ruleName}
                        onChange={this.handleChange}
                    />
                </h2>
                <hr />
                <h2><span style={{color: "#00ff00"}}>achievement condition(s):</span></h2>
                <button 
                    name="addCondition"
                    onClick={this.addCondition}
                    >
                    add condition
                </button>
                <br />
                <br />
                {preparedConditions}
                <hr />
                <button>create rule</button>
            </div>
        );
    }
}
export default App;
