import React from "react";
import ConditionForm from "./formElements/ConditionForm";

class App extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            ruleName: "",
            ruleConditions: [
                {
                    conditionType: "",
                    conditionCount: "",
                    conditionObject: ""
                }
            ]
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event)
    {
        const {name, value} = event.target;
        this.setState(() => { return( {[name]: value} ); });
    }

    render()
    {
        return(
            <div>
                <h1>Ivolunteer rule editor prototype</h1>
                <hr />
                <h2><span style={{color: "#ff0000"}}>rule name: </span></h2>
                <h2>
                    <input 
                        name="ruleName"
                        id="ruleNameTxt" 
                        placeholder="enter the name of your rule" 
                        type="text" 
                        value={this.state.ruleName}
                        onChange={this.handleChange}
                    />
                </h2>
                <hr />
                <h2><span style={{color: "#00ff00"}}>achievement condition(s):</span></h2>
                <button name="addCondition">add condition</button>
                <br />
                <br />
                <ConditionForm />
                <hr />
                <button>create rule</button>
            </div>
        );
    }
}
export default App;
