import React from "react";
import ConditionForm from "./formElements/ConditionForm";

class App extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            ruleName: "",
            ruleConditions: [
                {
                    conditionType: "requires",
                    conditionCount: "1",
                    conditionObject: "-- select something --"
                }
            ]
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event)
    {
        const {name, value} = event.target;
        this.setState(() => { return( {[name]: value} ); });
    }
