    render()
    {
        console.log(this);
        console.log(this.state);
        console.log(this.state.ruleConditions);
        console.log("=================================");
        let preparedConditions = []
        for (let i = 0; i < this.state.ruleConditions.length; i++)
        {
            preparedConditions.push(
                <ConditionForm 
                    key={i} 
                    conditionType={this.ruleConditions[i].conditionType}
                    conditionCount={this.ruleConditions[i].conditionCount}
                    conditionObject={this.ruleConditions[i].conditionObject}
                    onRemove={this.removeCondition}
                />
            );
        }


        return(
            <div>
                <h1>Ivolunteer rule editor prototype</h1>
                <hr />
                <h2><span style={{color: "#ff0000"}}>rule name: </span></h2>
                <h2>
                    <input 
                        name="ruleName"
                        id="ruleNameTxt" 
                        placeholder="enter the name of your rule" 
                        type="text" 
                        value={this.state.ruleName}
                        onChange={this.handleChange}
                    />
                </h2>
                <hr />
                <h2><span style={{color: "#00ff00"}}>achievement condition(s):</span></h2>
                <button 
                    name="addCondition"
                    onClick={this.addCondition}
                    >
                    add condition
                </button>
                <br />
                <br />
                {preparedConditions}
                <hr />
                <button>create rule</button>
            </div>
        );
    }
}
export default App;
