import React from "react";
import ConditionForm from "./formElements/ConditionForm";
import RuleName from "./formElements/RuleName";
import Conditions from "./formElements/Conditions";

class App extends React.Component
{
    constructor()
    {
        super();
        this.state = {
            ruleName: "",
            ruleConditions: [
                {
                    conditionType: "",
                    conditionCount: 1,
                    conditionObject: ""
                },
                {
                    conditionType: "",
                    conditionCount: 1,
                    conditionObject: ""
                }
            ]
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event)
    {
        const{name, value} = event.target;
        this.setState(() => { return( {[name]: value} ); });
    }
    handleAddCondition()
    {
        console.log("attempted to add condition");
    }

    render()
    {
        console.log("unwrapped", this.state.ruleConditions.length);
        console.log(this.state.ruleConditions);
        return(
            <div>
                <h1>Ivolunteer rule editor prototype</h1>
                <hr />
                <RuleName value={this.state.ruleName} onChange={this.handleChange}/>
                <hr />
                <Conditions 
                    onAdd={this.handleAddCondition} 
                    ruleConditions={this.state.ruleConditions}
                />
                <hr />
                <button>create rule</button>
            </div>
        );
    }
}




