        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeInForm = this.handleChangeInForm.bind(this);
    }

    handleChange(event)
    {
        const {name, value} = event.target;
        this.setState(() => { return( {[name]: value} ); });
    }
    handleChangeInForm(event)
    {
        const {name, value, formindex} = event.target;
        this.setState((prevState) => {
            let newRuleConditions = prevState.ruleConditions.slice();
            console.log(name);
            let condition = {
                [name]: value
            }
            newRuleConditions[formindex] = condition;

            console.log(prevState);
            console.log({ruleConditions: newRuleConditions});

            return ({ruleConditions: newRuleConditions});
        });
        console.log("hit end");
    }

    addCondition()
    {

    }
